                wrap = me.fastWrapLayout(wrap, mid);
                size = me.getContainersSize(wrap);
                iterations.push({
                    containers: wrap,
                    delta: Math.abs(aspect - size.aspect),
                    size: size
                });

                if (aspect < size.aspect) {
                    left = mid;
                } else {
                    right = mid;
                }
