                    pt.on('restored:snapshot', me.onSnapshotRestored, me);
                    pt.on('broke:reference', me.onBrokeReference, me);
                    pt.on(/changed:field:.*/, me.onFieldsChanged, me);
                    pt.on('added:field', me.onFieldAdded, me);
