                    panelCanvas = pageCanvas.g({
                        id: me.id + '-panel-canvas-' + pageIndex + '-' + i,
                        transform: me.panelCanvasTransform(panel, option.position)
                    });
                panelCanvas.node.setAttribute('clip-path', Snap.url(clipPath));

